from machine import Pin, RTC, PWM, I2Cfrom neopixel import NeoPixelfrom utime import sleep_ms# GPIO5 - yellow, GPIO4 - blue, GPIO0 - green, GPIO2 - red# GPIO14 - np scl = const(13)sda = const(0)class House():    led = const(14)  yellow = const(5)  blue = const(4)  red = const(2)  green = const(12)    def __init__(self):    self.timer = RTC()    self.np = NeoPixel(Pin(House.led), 25)    self.yel_but = Pin(House.yellow, Pin.IN)    self.blu_but = Pin(House.blue, Pin.IN)    self.red_but = Pin(House.red, Pin.IN)    self.gre_but = Pin(House.green, Pin.IN)    self.beep = Pin(15, Pin.OUT)    self.beep_play = PWM(self.beep)    # время, в течени    self.time_to_action = 5    self.fill_field = [0] * 25    self.speed = 5    # self.yel_but.irq(trigger=Pin.IRQ_FALLING, handler=self.callback)      # def callback(self, obj):  #   # self.np.fill((0,0,0))  #   # self.np.write()  #   self.led_on((15, 15, 0))  #   del self.yel_bu  #   self.run()  def leds_on(self, color):    self.np.fill(color)    self.np.write()  def led_on(self, pos, color):    self.np[pos] = color    self.np.write()  def beep_off(self):    self.beep_play.freq(0)    self.beep_play.duty(0)  def run_up(self, color):    if self.fill_field.count(0) == 0:        self.fill_field = [0] * 25        self.np.fill((0,0,0))        self.np.write()    count = 0    for each in range(self.fill_field.count(0)):        if count == 0:            self.fill_field[each] = color            self.led_on(each, color)        else:            self.fill_field[each-1] = 0            self.led_on(each-1, (0, 0, 0))            self.fill_field[each] = color            self.led_on(each, color)        sleep_ms(100)        count += 1    self.beep_off()    def run(self):    print('Run')    print('During work to 10 cycles')    self.timer.alarm(0, self.time_to_action*1000)    while self.timer.alarm_left() > 0:      if self.blu_but.value():        print('Blue')        self.beep_play.freq(0)        self.beep_play.duty(512)        # self.led_on((0, 0, 20))        self.run_up(color=(0, 0, 20))      elif self.gre_but.value():        print('Green')        self.beep_play.freq(2)        self.beep_play.duty(512)        # self.led_on((0, 20, 0))        self.run_up(color=(0, 20, 0))      elif self.red_but.value():        print('Red')        self.beep_play.freq(4)        self.beep_play.duty(512)        # self.led_on((20, 0, 0))        self.run_up(color=(20, 0, 0))      elif self.yel_but.value():        self.beep_play.freq(8)        self.beep_play.duty(512)        # self.led_on((20, 20, 0))        self.run_up(color=(20, 20, 0))    # self.np.fill((0,0,0))    # self.np.write()        # Start dev = House()while True:  print('Working')  dev.run()